package org.example;// 1부터 n까지 소수의 갯수를 출력하는 함수 만들기// 소수 검색 가능// 소수 구하기 x, 자바 소수 x//짝수 판별기는 참고용// 2번쨰 해야함class exam2 {    public static void main(String[] args) {        int rs = one_to_n_prime_numbers_count(10);        System.out.println("rs : " + rs);        // rs : 4        rs = one_to_n_prime_numbers_count(13);        System.out.println("rs : " + rs);        // rs :  6    }    static int one_to_n_prime_numbers_count(int n) {        int count = 0;        for(int i = 1; i<=n;i++) {            if(Math.isPrimeNumber(i)) {                System.out.println(i);                count++;            }        }        return count;    }}class Math {    static boolean isPrimeNumber(int a) {        if (a == 1) {            return false;        }        for (int i = 2; i < a; i++) {            if (a % i == 0) {                return false;            }        }        return true;    }}